package javaPackage;

public class String1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s="I am ajay rokade form Baramati pune";
		
		String s1="AJAYPATIL";
		
		System.out.println(s1.charAt(4));//P
		System.out.println(s1.compareTo(s));
		System.out.println(s1.compareToIgnoreCase(s));
		System.out.println(s1.concat(s));
		System.out.println(s1.contains(s));
		System.out.println(s1.endsWith(s));
		System.out.println(s1.startsWith(s));
		System.out.println(s1.equalsIgnoreCase(s1));//false
		System.out.println(s1.indexOf(3));//-1
		System.out.println(s1.indexOf("Y"));//3
		System.out.println(s1.isEmpty());//false
		System.out.println(s.toUpperCase());//
		System.out.println(s1.toLowerCase());
		System.out.println(s1.replace("B","A"));
		System.out.println(s1.split(s));
		System.out.println();
		
		
		
		
		

	}

}
